from functools import reduce

class Solution:
    def countMaxOrSubsets(self, nums: list[int]) -> int:
        mapping = defaultdict(int)
        max_or = 0
        for count in range(1, len(nums) + 1):
            subsets = combinations(range(len(nums)), count)
            for indices in subsets:
                subset = [nums[i] for i in indices]
                current_or = reduce(lambda a, b: a | b, subset)
                mapping[current_or] += 1
                max_or = max(max_or, current_or)
        return mapping[max_or]

def build_suffix_array(s):
    n = len(s)
    suffixes = sorted((s[i:], i) for i in range(n))
    suffix_array = [suffix[1] for suffix in suffixes]

    # Kasai's algorithm to compute LCP
    rank = [0] * n
