    int[] rank;
    int size;

    public DSU(int n) {
        par = new int[n];
        rank = new int[n];
        for (int i = 0; i < n; i++) {
            par[i] = i;
            rank[i] = 1;
        }
        this.size = n;
    }

    public int find(int u) {
