        int vv = find(v);
        if (uu == vv) {
            return false;
        }
        if (rank[uu] > rank[vv]) {
            par[vv] = uu;
        } else if (rank[vv] > rank[uu]) {
            par[uu] = vv;
        } else {
            par[uu] = vv;
            rank[vv]++;
        }
        this.size--;
        return true;
    }

    public int getSize() {
