def solution(a, b, n):
    # Helper function to calculate GCD using Euclidean algorithm
    def gcd(x, y):
        while y:
            x, y = y, x % y
        return x

    # Check if solution exists using Bézout's identity
    g = gcd(a, b)

    # If n is not divisible by gcd(a,b), no integer solutions exist
    if n % g != 0:
        return "No solution"

    # Use extended Euclidean algorithm to find one particular solution
    def extended_gcd(x1, y1):
        # Base case: when b = 0, we have ax + by = gcd(a,b)
        if b == 0:
            return (x1, 0)

        # Recursive call with updated values
        x2, y2 = extended_gcd(y1 % x1, x1)

        # Update coefficients using the recurrence relation
        x = y2
        y = x2 - (y1 // x1) * y2

        return (x, y)

    # Find one particular solution for ax + by = gcd(a,b)
    if a == 0 and b == 0:
        if n == 0:
            return ("x = ", 0, ", y = ", 0)
        else:
            return "No solution"

    x1, y1 = extended_gcd(a, b)

    # Scale the particular solution to get a solution for ax + by = n
    scale = n // g
    x = x1 * scale
    y = y1 * scale

    return ("x = ", x, ", y = ", y)

