def is_majority(arr, n, x):
    low = 0
    high = n - 1
    while low <= high:
        mid = (low + high) // 2
        if arr[mid] < x:
            low = mid + 1
        elif arr[mid] > x:
            high = mid - 1
        else:
            if mid == 0 or arr[mid - 1] != x:
                break
            else:
                high = mid - 1

    if low > high:
        return False

    first_occurrence = low
    low = 0
    high = n - 1
    while low <= high:
        mid = (low + high) // 2
        if arr[mid] < x:
            low = mid + 1
        elif arr[mid] > x:
            high = mid - 1
        else:
            if mid == n - 1 or arr[mid + 1] != x:
                break
            else:
                low = mid + 1

    last_occurrence = high

    count = last_occurrence - first_occurrence + 1

    return count > n // 2

