public:
    bool checkValidGrid(vector<vector<int>>& grid) {
        int n = grid.size();
        if (grid[0][0] != 0) return false; // Knight must start at top-left corner

        vector<pair<int, int>> moves = {{-2, 1}, {-1, 2}, {1, 2}, {2, 1}, {2, -1}, {1, -2}, {-1, -2}, {-2, -1}};
        return helper(grid, 0, 0, 1, moves);
    }

private:
    bool helper(vector<vector<int>>& grid, int row, int col, int num, vector<pair<int, int>>& moves) {
        if (num == grid.size() * grid.size()) return true;

        for (const auto& move : moves) {
            int i = row + move.first;
            int j = col + move.second;
            if (i >= 0 && i < grid.size() && j >= 0 && j < grid.size() && grid[i][j] == num) {
                return helper(grid, i, j, num + 1, moves);
            }
        }
        return false;
    }
};

