    def __init__(self):
        self.heap = []
        self.value_to_index = {}

    def insert(self, key, value):
        self.heap.append((key, value))
        index = len(self.heap) - 1
        self.value_to_index[value] = index
        self._bubble_up(index)

    def extract_min(self):
        if not self.heap:
            return None
        min_item = self.heap[0]
        last_item = self.heap.pop()
        if self.heap:
            self.heap[0] = last_item
            self.value_to_index[last_item[1]] = 0
            self._heapify_down(0)
        del self.value_to_index[min_item[1]]
        return min_item

    def peek(self):
        if not self.heap:
            return None
        return self.heap[0]

    def decrease_key(self, value, new_key):
        index = self.value_to_index.get(value)
        if index is None or new_key >= self.heap[index][0]:
            return
        self.heap[index] = (new_key, value)
        self._bubble_up(index)

    def _bubble_up(self, index):
        while index > 0:
            parent_index = (index - 1) // 2
            if self.heap[parent_index][0] <= self.heap[index][0]:
                break
            self.heap[index], self.heap[parent_index] = self.heap[parent_index], self.heap[index]
            self.value_to_index[self.heap[index][1]] = index
            self.value_to_index[self.heap[parent_index][1]] = parent_index
            index = parent_index

    def _heapify_down(self, index):
        while True:
            left_child_index = 2 * index + 1
            right_child_index = 2 * index + 2
            smallest_index = index

            if (left_child_index < len(self.heap) and 
                self.heap[left_child_index][0] < self.heap[smallest_index][0]):
