            if (right_child_index < len(self.heap) and 
                self.heap[right_child_index][0] < self.heap[smallest_index][0]):
                smallest_index = right_child_index

            if smallest_index == index:
                break

            self.heap[index], self.heap[smallest_index] = self.heap[smallest_index], self.heap[index]
            self.value_to_index[self.heap[index][1]] = index
            self.value_to_index[self.heap[smallest_index][1]] = smallest_index
            index = smallest_index

