import sys

def is_bipartite(n, edges):
    graph = [[] for _ in range(n)]
    for u, v in edges:
        graph[u-1].append(v-1)
        graph[v-1].append(u-1)

    color = [-1] * n
    components = []

    def bfs(start):
        queue = deque([start])
        color[start] = 0
        component = [start]
        while queue:
            node = queue.popleft()
            for neighbor in graph[node]:
                if color[neighbor] == -1:
                    color[neighbor] = 1 - color[node]
                    queue.append(neighbor)
                    component.append(neighbor)
                elif color[neighbor] == color[node]:
                    return False, []
        return True, component

    for i in range(n):
        if color[i] == -1:
            bipartite, component = bfs(i)
            if not bipartite:
                return -1
            components.append(component)

    max_j_count = 0
    for component in components:
        count_0 = sum(1 for node in component if color[node] == 0)
        count_1 = len(component) - count_0
        max_j_count += min(count_0, count_1)

    return max_j_count

